/**
 * Retrieves the URL for a specific badge by its name.
 * The lookup is case-insensitive.
 *
 * @param badgeName - The name of the badge to retrieve.
 * @returns The URL of the badge, or undefined if not found.
 */
export function getBadge(badgeName: string): string | undefined {
  return badgeMap[badgeName.toLowerCase()];
}

